INSERT INTO `retouch` (id,description,title,order_number,retouch_code,app_type) VALUES (1,'<p>Météo à la grisaille, pluvieuse ou ciel fade ?</p><p>-    Nous appliquons un ciel bleu de saison.</p><p>-    Nous adaptons la chromie aux reflets : vitrages, rivières, fleuves, mers...</p><p>-    Nous « séchons » les terrasses ou trottoirs...</p>','CIEL BLEU',10,NULL,'application.type.emmobilier'),
 (2,'<p>Pour les photos d’intérieur.</p><p>Rangement virtuel :</p><p>- Nous rangeons les effets personnels,</p><p>- Nous équilibrons chromie &amp; lumière, </p><p>- Nous récupérons les vues extérieures.</p><p>- Pas de correction des distorsions optiques.</p>','PERTINENCE 360°',3,NULL,'application.type.emmobilier'),
 (3,'<p>Vider / rénover</p><p>- Nous vidons intégralement la pièce, Nous enlevons les rideaux uniquement quand la vue extérieure et les huisseries sont visibles, pour reconstituer.</p><p>- Nous rénovons sols, murs et plafonds, suivant vos demandes, celles de vos clients ou des échantillons présélectionnés.</p><p>- Vous pouvez désormais vous projeter dans le bien actuel, vidé de son contenu, avec vos futurs choix de couleur et de matière.</p>','PHOTO-STAGING 2D',6,NULL,'application.type.emmobilier'),
 (4,'<p>Le contenu de votre photo est bon, mais le rendu de l’image insatisfaisant :</p>\r\n<p>* Nous équilibrons la chromie, la lumière.</p>\r\n<p>* Nous corrigeons les distorsions optiques.</p>\r\n<p>* Nous récupérons (débouchons) les vues extérieures.</p>\r\n<p>* Le contenu de l’image n’est pas retouché.</p>','ÉQUILIBRAGE',5,NULL,'application.type.emmobilier'),
 (5,'','PHOTO-STAGING VIRTUEL',7,NULL,'application.type.emmobilier'),
 (6,'<p>Pour les photos d’intérieur.</p><p>Rangement et désencombrement virtuels :</p><p>- Nous rangeons les effets personnels,</p><p>- Nous désencombrons l’agencement intérieur,</p><p>- Nous équilibrons chromie &amp; lumière,</p><p><span style="font-size: 0.9375rem;">- Nous corrigeons les distorsions optiques,</span></p><p><span style="font-size: 0.9375rem;">- Nous récupérons les vues extérieures.</span><br></p><p><span style="font-size: 0.9375rem;">Cette prestation a ses limites, elle nécessite de pouvoir reconstituer les matières, les formes, le style, le design du mobilier …</span><br></p>','PERTINENCE « INTÉRIEUR »',1,NULL,'application.type.emmobilier'),
 (7,'<p>Pour les photos d’extérieur.\r\nRangement et désencombrement virtuels :</p>\r\n<p>- Nous rangeons les effets personnels,</p>\r\n<p>- Nous désencombrons l’agencement extérieur,</p>\r\n<p>- Nous équilibrons chromie &amp; lumière, </p>\r\n<p>- Nous corrigeons les distorsions optiques,</p>','PERTINENCE « EXTÉRIEUR »',2,NULL,'application.type.emmobilier'),
 (8,'<p>Pour les photos d’extérieur.</p>\r\n<p>-    Nous aménageons vos espaces extérieurs suivant vos demandes,</p>\r\n<p>-    Nous créons les pelouses inexistantes,</p>\r\n<p>-    Nous agrémentons vos espaces d’ornementation florale, d’arbustes,</p>\r\n<p>-    Nous incrustons les mobiliers de jardin,</p>\r\n<p>-    Nous travaillons les façades des biens,</p>','AMÉNAGEMENT « EXTÉRIEUR »',4,NULL,'application.type.emmobilier'),
 (9,'<p>Photos d’extérieur.</p>\r\n<p>Pour des ambiances raffinées, ou cosy de fin de journée.</p>\r\n<p>-    Nous appliquons un ciel de tombée du jour,</p>\r\n<p>-    Nous adaptons la chromie aux reflets : vitrages, rivières, fleuves, mers...</p>\r\n<p>-    Nous allumons vos luminaires extérieurs,</p>\r\n<p>-    Nous mettons en valeur vos piscines.</p>','AMBIANCE de CRÉPUSCULE',9,NULL,'application.type.emmobilier'),
